import java.util.HashMap;
import java.util.Map;

import com.auth0.jwt.JWT;
import com.auth0.jwt.JWTVerifier;
import com.auth0.jwt.algorithms.Algorithm;
import com.auth0.jwt.interfaces.Claim;
import com.auth0.jwt.interfaces.DecodedJWT;

public class JwtToken {

    private static String MY_SECRET = "hifee_face";// 自己的加盐部分

    public static String createToken() throws Exception {
        Map<String, Object> map = new HashMap<String, Object>();
        // 加密头部分
        map.put("alg", "HS256");
        map.put("typ", "JWT");
        // payload 数据部分
        String token = JWT.create().withHeader(map)// header
                .withClaim("name", "wuli")// payload
                .withClaim("age", "18age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1")
                .withClaim("age1", "18age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1")
                .withClaim("age2", "18age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1age1")
                .withClaim("age3", "")
                .withClaim("age4", "")
                .withClaim("age5", "")
                .withClaim("age6", "")
                .withClaim("age7", "")
                .withClaim("age8", "")
                .sign(Algorithm.HMAC256(MY_SECRET));// 自定义部分
        return token;
    }

    public static void verifyToken(String token, String key) throws Exception {
        JWTVerifier verifier = JWT.require(Algorithm.HMAC256(key)).build();
        DecodedJWT jwt = verifier.verify(token);
        Map<String, Claim> claims = jwt.getClaims();
        // 解密
        System.out.println(claims.get("name").asString());
    }

    public static void main(String[] args) throws Exception {
        String str = createToken();
        System.out.println(str);
        verifyToken(str, MY_SECRET);

    }
}


